function xT(arr, rk, ck) {
  const rows = {}, cs = new Set();
  arr.forEach(i => { const r = i[rk] || "Otro", c = i[ck] || "Otro"; cs.add(c); if (!rows[r]) rows[r] = {}; rows[r][c] = (rows[r][c] || 0) + 1; });
  const cols = [...cs].sort();
  const rd = Object.entries(rows).map(([name, v]) => ({ name, ...v, total: Object.values(v).reduce((a, b) => a + b, 0) })).sort((a, b) => b.total - a.total);
  return { cols, rd };
}

function parseCSV(text) {
  const lines = text.split("\n").filter(l => l.trim());
  let hi = 0;
  for (let i = 0; i < Math.min(lines.length, 5); i++) { if (lines[i].includes("Id Empleado")) { hi = i; break; } }
  const h = lines[hi].split(",").map(s => s.trim().replace(/\r/g, ""));
  const ix = { id: h.indexOf("Id Empleado"), p: h.indexOf("Glosa Puesto"), e: h.indexOf("Glosa Empresa"), fn: h.indexOf("Funcionalidad"), ub: h.indexOf("Glosa Ubicación"), cc: h.indexOf("Centro de Costo"), s: h.indexOf("Sexo") };
  if (ix.id === -1) return null;
  const rec = {};
  for (let i = hi + 1; i < lines.length; i++) {
    const c = lines[i].split(",").map(s => s.trim().replace(/\r/g, ""));
    const rut = c[ix.id]; if (!rut) continue;
    rec[rut] = { p: ix.p >= 0 ? c[ix.p] : null, e: ix.e >= 0 ? c[ix.e] : null, fn: ix.fn >= 0 ? c[ix.fn] : null, ub: ix.ub >= 0 ? c[ix.ub] : null, cc: ix.cc >= 0 ? c[ix.cc] : null, s: ix.s >= 0 ? c[ix.s] : null };
  }
  return rec;
}

function applyCSV(vRec) {
  const ruts = new Set(Object.keys(vRec));
  return BASE.filter(r => ruts.has(r.rut)).map(r => {
    const v = vRec[r.rut]; if (!v) return r;
    let uen = r.uen;
    if (v.cc && CCL[v.cc]) { uen = CCL[v.cc]; if (uen === "VSPT Chile (NO PPTO)") uen = "VSPT Chile"; if (uen === "Expat/MBA") uen = "Matriz"; }
    let fn = v.fn || r.funcionalidad;
    if (TR.has(r.rut) || fn === "No Aplica") fn = "Trade Marketing";
    return { ...r, puesto: v.p || r.puesto, empresa: v.e || r.empresa, ubicacion: v.ub || r.ubicacion, uen, funcionalidad: fn, sexo: v.s || r.sexo };
  });
}

